INSERT INTO patient_notes(note, doctor_id, patient_id) VALUES ("Symptoms of bipolar disorder depend on which mood you're experiencing. Unlike simple mood swings, each extreme episode of bipolar disorder can last for several weeks (or even longer), and some people may not experience a normal mood very often.", 1, 20);
INSERT INTO patient_notes(note, doctor_id, patient_id) VALUES ("You may initially be diagnosed with clinical depression before having a future manic episode (sometimes years later), after which you may be diagnosed with bipolar disorder.", 1, 20);
INSERT INTO patient_notes(note, doctor_id, patient_id) VALUES ("During an episode of depression, you may have overwhelming feelings of worthlessness, which can potentially lead to thoughts of suicide.", 1, 20);
INSERT INTO patient_notes(note, doctor_id, patient_id) VALUES ("If you're feeling suicidal or having severe depressive symptoms, contact your GP, care co-ordinator or local mental health emergency services as soon as possible.", 1, 20);
INSERT INTO patient_notes(note, doctor_id, patient_id) VALUES ("During a manic phase of bipolar disorder, you may feel very happy and have lots of energy, ambitious plans and ideas. You may spend large amounts of money on things you can't afford and wouldn't normally want.", 1, 20);
INSERT INTO patient_notes(note, doctor_id, patient_id) VALUES ("Not feeling like eating or sleeping, talking quickly and becoming annoyed easily are also common characteristics of this phase.", 1, 20);
INSERT INTO patient_notes(note, doctor_id, patient_id) VALUES ("medication to prevent episodes of mania, hypomania (less severe mania) and depression – these are known as mood stabilisers and are taken every day on a long-term basis", 1, 20);
INSERT INTO patient_notes(note, doctor_id, patient_id) VALUES ("psychological treatment – such as talking therapy, which can help you deal with depression, and provides advice about how to improve your relationships", 1, 20);